			source.offsetX += forceX;
			source.offsetY += forceY;
			target.offsetX -= forceX;
			target.offsetY -= forceY;

			var s = "Edge force between nodes " + source.id + " and " + target.id;
			s += "\nDistance: " + l + " Force: (" + forceX + ", " + forceY + ")";
